import { Observable, tap } from 'rxjs';

@Component({
  selector: 'app-some-table',
  templateUrl: './some-table.component.html',
  styleUrls: ['./some-table.component.css'],
})
export class SomeTableComponent{
  //myForm: FormGroup;
