import { Component, Input, OnInit } from '@angular/core';
import { FormGroup } from '@angular/forms';

//import { ValidateUrl, emailDomain } from '../validator';
import { FormService } from '../../service/form.service';
import { Observable, tap } from 'rxjs';

@Component({
  selector: 'app-some-table',
  templateUrl: './some-table.component.html',
  styleUrls: ['./some-table.component.css'],
})
export class SomeTableComponent implements OnInit {
  //myForm: FormGroup;
  public myForm: FormGroup = this.formService.tableForm;


  @Input() tableForm!: FormGroup;

  public tableForm$: Observable<FormGroup<TableFormAreas>> = this.tableFacade.tableForm$.pipe(tap(v => console.log('tap: ', v)));

  constructor(private formService: FormService) {}

  ngOnInit() {
    this.myForm = this.formService.tableForm;
    console.log('My form: ',)
  }
}
<p>some-table works!</p>

  <div formArrayName="areas">

    <div *ngFor="let area of tableForm.controls['areas'].controls; let areaIndex=index;">
        <div [formGroupName]="areaIndex">
            <input formControlName="area" />
            <div formArrayName="rows">
                {{ area.controls.area.value }}
                <div *ngFor="let row of area.controls['rows'].controls; let rowIndex=index">
                    <div [formGroupName]="rowIndex">
                        <input formControlName="tableCounter" />
                    </div>

                </div>
            </div>
        </div>
    </div>



  </div>
